import { Component } from '@angular/core';
import { Products } from '../products';
import { UserService } from '../user.service';
import { Router } from '@angular/router';
import { Users } from '../users';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-productslist',
  templateUrl: './productslist.component.html',
  styleUrls: ['./productslist.component.css']
})
export class ProductslistComponent {

    products:Products[]=[];
    cartid:number|undefined;


    constructor(private userService : UserService,private router:Router,private loginservice:LoginService)
    { }

    ngOnInit():void
    {
      this.getproducts();
    }


    private getproducts()
    {
      this.userService.getProductsList().subscribe(data=>
        {
          this.products=data;
        });
    }

    updateproduct(id:number)
    {
        this.router.navigate(['update-product',id]);
    }


    deleteproduct(id:number)
    {
       this.userService.deleteproduct(id).subscribe(data=>{alert("product removed");
        this.getproducts()});
    }

    addtocart(cartid:number){

    }
  }


<nav class="navbar navbar-expand-lg navbar-dark bg-info">
    <a class="navbar-brand" href="#"> <img class="imgicon" src="https://cdn-icons-png.flaticon.com/512/5470/5470044.png?ga=GA1.1.979865094.1697005373" ></a>
    <h1 class="text-center"  align="center">productslist</h1>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
  </nav><div class="back">
<div class="container">
    <div class="row">
      <div class="col-md-12">

<table class="table table-striped table-bordered">
    <thead class="thead-dark">
        <tr>
            <th>S.No</th>
            <th>product_id</th>
            <th>product_name</th>
            <th>product_price</th>
            <th>category</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let product of products, let i=index">
            <td>{{i+1}}</td>
            <td>{{product.product_id}}</td>
            <td>{{product.productName}}</td>
            <td>{{product.product_price}}</td>
            <td>{{product.category.name}}</td>
            <button class="btn btn-primary m-2" (click)="updateproduct(product.product_id)">Update</button>
            <button class="btn btn-danger m-2" (click)="deleteproduct(product.product_id)">Delete</button>
        </tr>
    </tbody>
</table>
      </div>
    </div>
</div>
</div>
