import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Products } from '../products';

@Component({
  selector: 'app-create-product',
  templateUrl: './create-product.component.html',
  styleUrls: ['./create-product.component.css']
})
export class CreateProductComponent {
  id!:number;
  product:Products=new Products()

  constructor(private userService : UserService,private router:Router,
    private route:ActivatedRoute)
  {}

  ngOnInit(): void {  }

  saveProduct()
  {
    this.userService.createProduct(this.product).subscribe(data=>{console.log(data); this.goToProductList()},error=>console.log(error));
  }


  goToProductList()
  {
    this.router.navigate(['products']);
  }

  onSubmit()
  {
    console.log(this.product);
    this.saveProduct();
  }
}
<h1 class="text-center">create-product</h1>
<div>
    <form
        (ngSubmit)="onSubmit()">
        <div class="form-group">
            <label>product_name</label>
            <input type="text" class="form-control" id="username" name="username" [(ngModel)]="product.product_name">
        </div>
        <div class="form-group">
            <label>product_price</label>
            <input type="text" class="form-control" id="email" name="email" [(ngModel)]="product.product_price">
        </div>
   <br/>
   <button type="submit " class="btn btn-success">Submit</button>
</form>
</div>
