import { Component } from '@angular/core';
import { Products } from '../products';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-productslist',
  templateUrl: './productslist.component.html',
  styleUrls: ['./productslist.component.css']
})
export class ProductslistComponent {

    products:Products[]=[];


    constructor(private userService : UserService,private router:Router)
    {}

    ngOnInit():void
    {
      this.getproducts();
    }


    private getproducts()
    {
      this.userService.getProductsList().subscribe(data=>
        {
          this.products=data;
        });
    }
    updateUser(id:number)
    {
        this.router.navigate(['update-user',id]);
    }


    deleteUser(id:number)
    {
       this.userService.deleteuser(id).subscribe(data=>{alert("user removed")
        ;this.getproducts();});
    }
  }


<p>productslist works!</p>
<h1 calss="text-center">users list</h1>
<table class="table table-striped table-bordered">
    <thead>
        <tr>
            <th>product_name</th>
            <th>product_price</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let product of products">
            <td>{{product.product_name}}</td>
            <td>{{product.product_price}}</td>

            <button class="btn btn-primary m-2" (click)="updateUser(product.product_id)">Update</button>
            <button class="btn btn-danger m-2" (click)="deleteUser(product.product_id)">Delete</button>
        </tr>
    </tbody>
