import { Injectable } from '@angular/core';
import { Users } from './users';
import { Cart } from './cart';
import { UserService } from './user.service';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class LoginService {

 private user:Users | null=null;

 private cart:Cart | null=null;
  constructor(private httpClient:HttpClient,private userservice:UserService) { }
  private baseUrl1="http://localhost:8080/user/login";

  private isAuthenticated = false;

    login(user:Users):Observable<object>{
      return this.httpClient.post(`${this.baseUrl1}`,user);this.isAuthenticated = true;
    }

  logout() {
    this.isAuthenticated = false,this.user=null;;
  }

  isAuthenticatedUser(): boolean {
    return this.isAuthenticated;
  }

  setUserLogin(user: Users) {
    this.user = user;
  }

  getUserLogin(): Users | null {
    return this.user;
  }

  getuserforCart():Users | null
  {
    return this.user;
  }

  getUserproducts(cart:Cart){
    return this.cart;
  }


}
