import { Component } from '@angular/core';
import { Category } from '../category';
import { UserService } from '../user.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-update-category',
  templateUrl: './update-category.component.html',
  styleUrls: ['./update-category.component.css']
})
export class UpdateCategoryComponent {

  id!:number;
  category:Category=new Category()

  constructor(private userService : UserService,private router:Router,
    private route:ActivatedRoute)
  {}

  ngOnInit(): void {
      this.category=new Category();
      this.id=this.route.snapshot.params['id'];

      this.userService.getCategoryById(this.id)
      .subscribe(data=>{this.category=data;},error=>console.log(error));
  }

  onSubmit(){
    this.updatecategory();
  }

  updatecategory(){
    this.userService.updatecategory(this.id,this.category)
    .subscribe(data=>{this.gotocategoryList()});
  }

  gotocategoryList(){
    this.router.navigate(['categories']);
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-info">
    <a class="navbar-brand" href="#"> <img class="imgicon" src="https://cdn-icons-png.flaticon.com/512/5470/5470044.png?ga=GA1.1.979865094.1697005373" ></a>
    <h1 class="text-center mr-auto">productslist</h1>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
  </nav>
<h1 class="text-center">create-product</h1>
<div>
    <form
        (ngSubmit)="onSubmit()">
        <div class="form-group">
            <label>category_name</label>
            <input type="text" class="form-control" id="username" name="username" [(ngModel)]="category.name">
        </div>
   <br/>
   <button type="submit" class="btn btn-success">Submit</button>
</form>
</div>
