import { Component } from '@angular/core';
import { Cart } from '../cart';
import { UserService } from '../user.service';
import { Router } from '@angular/router';
import { Products } from '../products';
import { LoginService } from '../login.service';


@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  number:number=0;
  userId:number | undefined;

  product:Products[]=[];
  carts:Cart[]=[];


  constructor(private userService : UserService,private router:Router,private loginservice:LoginService) {}  
     //this.userId=this.loginservice.getUserLogin()?.user_id;
  ngOnInit():void
  {
    this.getUsers();
    this.getproducts();
    this.getUserId();
  }

  private getproducts()
    {
      this.userService.getCartList().subscribe(data=>
        {
          this.carts=data;
        });
    }

    private getUserId(){
      this.userService.getCartItems(this.userId).subscribe(data => {
        this.carts = data;
      });
    }

  private getUsers()
  {
    this.userService.getCartList().subscribe(data=>
      {
        this.carts=data;
      });
  }

}
<nav class="navbar navbar-expand-lg navbar-dark bg-info">
    <a class="navbar-brand" href="#"> <img class="imgicon" src="https://cdn-icons-png.flaticon.com/512/5470/5470044.png?ga=GA1.1.979865094.1697005373" ></a>
    <h1 class="text-center mr-auto">users list</h1>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
  </nav>
<table class="table table-bordered">
    <thead>
        <tr>
            <th>S.No</th>
            <th>users</th>
            <th>products</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let cart of carts, let i=index,">
            <td>{{i+1}}</td>
            <td>{{cart.user.username}}</td>
            <td>
                <ul>
                    <li *ngFor="let products of cart.products.productName">{{products}}</li>
                  </ul>
            </td>
        </tr>
    </tbody>
</table>
