import { Component } from '@angular/core';
import { UserService } from '../user.service';
import { Router } from '@angular/router';
import { Category } from '../category';

@Component({
  selector: 'app-categorylist',
  templateUrl: './categorylist.component.html',
  styleUrls: ['./categorylist.component.css']
})
export class CategorylistComponent {

  categories:Category[]=[];


  constructor(private userService : UserService,private router:Router)
  {}

  ngOnInit():void
  {
    this.getcategories();
  }


  private getcategories()
  {
    this.userService.getCategoriesList().subscribe(data=>
      {
        this.categories=data;
      });
  }
  updatecategory(id:number)
  {
      this.router.navigate(['update-category',id]);
  }


  deletecategory(id:number)
  {
     this.userService.deletecategory(id).subscribe(data=>{alert("category removed")
      ;this.getcategories();});
  }
}
<h1 class="text-center">categorylist</h1>
<table class="table table-striped table-bordered">
    <thead>
        <tr>
            <th>category_id</th>
            <th>category_name</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let category of categories">
            <td>{{category.id}}</td>
            <td>{{category.name}}</td>

            <button class="btn btn-primary m-2" (click)="updatecategory(category.id)">Update</button>
            <button class="btn btn-danger m-2" (click)="deletecategory(category.id)">Delete</button>
        </tr>
    </tbody>
</table>
