import { Component } from '@angular/core';
import { Cart } from '../cart';
import { UserService } from '../user.service';
import { Router } from '@angular/router';
import { Products } from '../products';
import { LoginService } from '../login.service';


@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  number:number=0;
  userId:number | undefined;

  product:Products[]=[];
  carts:Cart[]=[];


  constructor(private userService : UserService,private router:Router,private loginservice:LoginService) {}  
     //this.userId=this.loginservice.getUserLogin()?.user_id;
  ngOnInit():void
  {
    this.getUsers();
    this.getproducts();
    this.getUserId();
  }

  private getproducts()
    {
      this.userService.getCartList().subscribe(data=>
        {
          this.carts=data;
        });
    }

    private getUserId(){
      this.userService.getCartItems(this.userId).subscribe(data => {
        this.carts = data;
      });
    }

  private getUsers()
  {
    this.userService.getCartList().subscribe(data=>
      {
        this.carts=data;
      });
  }

}
<h1 class="text-center" >users list</h1>
<table class="table table-bordered">
    <thead>
        <tr>
            <th>S.No</th>
            <th>users</th>
            <th>products</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let cart of carts, let i=index,">
            <td>{{i+1}}</td>
            <td>{{cart.user.username}}</td>
            <td>
                <ul>
                    <li *ngFor="let products of cart.products.productName">{{products}}</li>
                  </ul>
            </td>
        </tr>
    </tbody>
</table>
