import { Component } from '@angular/core';
import { Users } from '../users';
import { UserService } from '../user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-users-list',
  templateUrl: './users-list.component.html',
  styleUrls: ['./users-list.component.css']
})
export class UsersListComponent {

  number:number=0;
  users:Users[]=[];


  constructor(private userService : UserService,private router:Router)
  {}

  ngOnInit():void
  {
    this.getUsers();
  }


  private getUsers()
  {
    this.userService.getUsersList().subscribe(data=>
      {
        this.users=data;
      });
  }
  updateUser(id:number)
  {
      this.router.navigate(['update-user',id]);
  }


  deleteUser(id:number)
  {
     this.userService.deleteuser(id).subscribe(data=>{console.log(data),alert("user removed")
      ;this.getUsers();});
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-info">
    <a class="navbar-brand" href="#"> <img class="imgicon" src="https://cdn-icons-png.flaticon.com/512/5470/5470044.png?ga=GA1.1.979865094.1697005373" ></a>
    <h1 class="text-center mr-auto">userslist</h1>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
  </nav>
<table class="table table-striped table-bordered">
    <thead>
        <tr>
            <th>S.No</th>
            <th>Fullname</th>
            <th>username</th>
            <th>Email</th>
            <th>Mobile number</th>
            <th>address</th>
            <th>role</th>
            <th>status</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let user of users, let i=index">
            <td>{{i+1}}</td>
            <td>{{user.fullname}}</td>
            <td>{{user.username}}</td>
            <td>{{user.email}}</td>
            <td>{{user.number}}</td>
            <td>{{user.address}}</td>
            <td>{{user.role}}</td>
            <td>{{user.status}}</td>
            <button class="btn btn-primary m-2" (click)="updateUser(user.user_id)">Update</button>
            <button class="btn btn-danger m-2" (click)="deleteUser(user.user_id)">Delete</button>
        </tr>
    </tbody>
</table>
